import React from 'react';
import { Route, Link } from 'react-router-dom';
import SigninedNavbar from './signinedNavbar';
import SignoutedNavbar from './signoutedNavbar';
import ColContainer from './colContainer';
import axios from 'axios';
export default class Root extends React.Component {
  state = {
    isSignin : false
  }
  loginRequest = () => {
    axios.get('http://localhost:3000/auth/google')
    .then(function(response){
      console.log('I wanna log in!!',response);
    })
  }

  componentDidMount() {
    axios.get('http://localhost:3000/auth/signcheck')
    .then((response)=>{
      console.log('is it loged?',response);
      this.setState({...response.data});
    })
    .catch((reason)=>{
      console.log('ajax failed',reason);
    });
    axios.get('http://localhost:3000/set/data/'+this.props.match.params.categoryid)
    .then((response)=>{
      this.setState({...response.data, ajaxed : true});
      console.log('this is response__________',this.state);
      return response.data;
    });
  }
  render(){
    const isSignin = this.state.isSignin;
    console.log('this is server side information',this.props);
    let navBar;
    if (isSignin) {
      navBar = <SigninedNavbar userName={this.state.userName}/>;
    } else {
      navBar = <SignoutedNavbar />;
    }
    return(
      <div>
        {navBar}
        <ColContainer />
      </div>
    );
  }
}
